import React, { useState } from "react";
import styles from "../login/login.module.scss";
import { Link, useNavigate } from "react-router-dom";
import { useForm } from "react-hook-form";
import axios from "axios";
import Cookies from "universal-cookie/es6";

interface User {
  username: string;
  password: string;
  confirmPassword: string;
}

export const Register = () => {
  const {
    register,
    handleSubmit,
    watch,
    formState: { isValid, errors, isDirty },
  } = useForm<User>({ mode: "onChange" });

  const navigate = useNavigate();

  const [errMessage, setErrMessage] = useState("");
  const cookies = new Cookies();
  const dispatch = useDispatch();

  const onSubmit = async (data: User): Promise<void> => {
    const { username, password } = data;

    try {
      const response = await axios.post(
        "http://localhost:3333/auth/local/signup",
        JSON.stringify({ username, password }),
        {
          headers: { "Content-Type": "application/json" },
        }
      );
      const d = new Date();
      d.setMinutes(d.getMinutes() + 15);
      cookies.set("token", response?.data?.access_token, {
        path: "/",
        expires: d,
      });
      dispatch(setCredentials({ ...response?.data }));
      navigate("/");
      console.log(JSON.stringify(response?.data));
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <>
      <div className={styles.container}>
        <form className={styles.form} onSubmit={handleSubmit(onSubmit)}>
          <h1 className={styles["form__heading"]}>Register</h1>
          {errors.confirmPassword && (
            <p className={styles["form__error"]}>
              {errors.confirmPassword?.message}
            </p>
          )}
          {errors.password && (
            <p className={styles["form__error"]}>{errors.password?.message}</p>
          )}
          <label className={styles["form__label"]}>Username</label>
          <input
            className={styles["form__input"]}
            placeholder="Tomas"
            type="text"
            {...register("username", { required: true })}
          />
          <label className={styles["form__label"]}>Password</label>
          <input
            className={styles["form__input"]}
            placeholder="Tomas"
            type="password"
            {...register("password", { required: true })}
          />
          <label className={styles["form__label"]}>Confirm password</label>
          <input
            className={styles["form__input"]}
            placeholder="Tomas"
            type="password"
            {...register("confirmPassword", {
              required: true,
              validate: (val: string) => {
                if (watch("password") != val) {
                  return "Your passwords do no match";
                }
              },
            })}
          />
          <button
            className={styles["form__button"]}
            type="submit"
            disabled={!isValid}
          >
            Register
          </button>
          <span>
            Or{" "}
            <Link to="/login" className={styles["sign-up-link"]}>
              login
            </Link>
          </span>
        </form>
      </div>
    </>
  );
};
