import React, { useState } from "react";
import styles from "./movie-card.module.scss";

type Props = {
  movie: {
    title: string;
    description: string;
    releaseYear: number;
    runtime: number;
  };
  setIsModalActive: React.Dispatch<React.SetStateAction<boolean>>;
};

export const MovieCard = ({
  movie: { title, releaseYear, runtime, genre, description, trailer, image },
  setIsModalActive,
}: Props) => {
  return (
    // <div className={styles["movie-card"]}>
    //   <h3>{title}</h3>
    //   <p>{description}</p>
    //   <button
    //     className={styles["movie-card__button"]}
    //     onClick={(prev) => setIsModalActive(!prev)}
    //   >
    //     Read more
    //   </button>
    // </div>
    <div className={styles["movie-card"]}>
      <div className={styles["movie-card_left"]}>
        <img src={image} />
      </div>
      <div className={styles["movie-card_right"]}>
        <h1>{title}</h1>
        <div className={styles["movie-card_right__details"]}>
          <ul>
            <li>2003</li>
            <li>111 min</li>
            <li>Action</li>
          </ul>
        </div>
        <div className={styles["movie-card_right__description"]}>
          <p>{description}</p>
          <button className={styles["movie-card_right__button"]}>
            Read more
          </button>
          <div className={styles["movie-card_right__button"]}>
            <button className={styles["movie-card_right__button"]}>
              Watch trailer
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};
