import React, { useState, ReactChildren, ReactChild } from "react";
import { useLocation } from "react-router-dom";
import styles from "./fade-animation.module.scss";

export const FadeAnimation = ({ children }: React.Children) => {
  const location = useLocation();
  const [displayLocation, setDisplayLocation] = useState(location);
  const [transitionStage, setTransitionStage] = useState("fade-in");

  return (
    <div
      className={`
  ${styles["fade-animation"]}
  ${styles[`fade-animation--${transitionStage}`]}
`}
      onAnimationEnd={() => {
        if (transitionStage === "fade-out") {
          setTransitionStage("fade-in");
          setDisplayLocation(location);
        }
      }}
    >
      {children}
    </div>
  );
};
