import axios from "axios";
import { useState } from "react";
import { useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";
import Cookies from "universal-cookie";
import { setCredentials } from "../features/auth/authSlice";

interface User {
  username: string;
  password: string;
}

export const useAuth = () => {
  const [isLoading, setIsLoading] = useState(false);

  const signin = async ({ username, password }: User) => {
    setIsLoading(true);

    authApi("signin", username, password);

    // try {
    //   const response = await axios.post(
    //     "http://localhost:3333/auth/local/signin",
    //     JSON.stringify({ username, password }),
    //     {
    //       headers: { "Content-Type": "application/json" },
    //     }
    //   );
    //   const d = new Date();
    //   d.setMinutes(d.getMinutes() + 15);
    //   cookies.set("token", response?.data?.access_token, {
    //     path: "/",
    //     expires: d,
    //   });
    //   dispatch(setCredentials({ ...response?.data }));
    //   setIsLoading(false);
    //   navigate("/");
    //   console.log(JSON.stringify(response?.data));
    // } catch (err: any) {
    //   setIsLoading(false);

    //   if (err.response?.status === 403) {
    //     setErrorMsg("Wrong username or password");
    //   } else {
    //     setErrorMsg("Login failed");
    //   }
    // }
  };

  const signup = async ({ username, password }: User) => {
    authApi("signup", username, password);

    try {
      const response = await axios.post(
        "http://localhost:3333/auth/local/signup",
        JSON.stringify({ username, password }),
        {
          headers: { "Content-Type": "application/json" },
        }
      );
      const d = new Date();
      d.setMinutes(d.getMinutes() + 15);
      cookies.set("token", response?.data?.access_token, {
        path: "/",
        expires: d,
      });
      dispatch(setCredentials({ ...response?.data }));
      navigate("/");
      console.log(JSON.stringify(response?.data));
    } catch (err: any) {
      if (err.response?.status === 403) {
        setErrorMsg("Wrong username or password");
      } else {
        setErrorMsg("Login failed");
      }
    }
  };

  return { isLoading, errorMsg, signin, signup };
};

const authApi = async ({
  type,
  username,
  password,
}: {
  type: string;
  username: string;
  password: string;
}) => {
  const cookies = new Cookies();
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [errorMsg, setErrorMsg] = useState("");

  try {
    const response = await axios.post(
      `http://localhost:3333/auth/local/${type}`,
      JSON.stringify({ username, password }),
      {
        headers: { "Content-Type": "application/json" },
      }
    );
    const d = new Date();
    d.setMinutes(d.getMinutes() + 15);
    cookies.set("token", response?.data?.access_token, {
      path: "/",
      expires: d,
    });
    dispatch(setCredentials({ ...response?.data }));
    navigate("/");
    console.log(JSON.stringify(response?.data));
  } catch (err: any) {
    if (err.response?.status === 403) {
      setErrorMsg("Wrong username or password");
    } else {
      setErrorMsg("Login failed");
    }
  }
};
