import React, { useState } from "react";
import styles from "./movie-card.module.scss";

type Props = {
  movie: { title: string; description: string };
};

export const MovieCard = ({ movie: { title, description } }: Props) => {
  return (
    <div className={styles["movie-card"]}>
      <h3>{title}</h3>
      <p>{description}</p>
      <button
        className={styles["movie-card__button"]}
        onClick={(prev) => setIsModalActive(!prev)}
      >
        Read more
      </button>
    </div>
  );
};
