import React from "react";
import { Header } from "../../components/layout/header";
import styles from "./login.module.scss";
import { useNavigate } from "react-router-dom";
import { useForm } from "react-hook-form";
import { Heading } from "../../components/heading";

interface User {
  username: string;
  password: string;
}

type InputFields = Array<{
  name: string;
  placeholder: string;
  required?: boolean;
}>;

const inputFields: InputFields = [
  {
    name: "username",
    placeholder: "Tomas",
    required: true,
  },
  {
    name: "password",
    placeholder: "Tomas",
    required: true,
  },
];

export const Login = () => {
  const {
    register,
    handleSubmit,
    formState: { errors, isValid },
  } = useForm<User>({ mode: "onChange" });

  const navigate = useNavigate();

  const onSubmit = (data: User): void => {
    console.log(data);
    navigate("/");
  };

  return (
    <>
      <div className={styles.container}>
        <form className={styles.form} onSubmit={handleSubmit(onSubmit)}>
          <Heading tag="h1">Login</Heading>
          {inputFields.map(({ name, placeholder, required }) => {
            return (
              <>
                <label className={styles["form__label"]}>
                  {name.toUpperCase()}
                </label>
                <input
                  className={styles["form__input"]}
                  placeholder={placeholder}
                  type="text"
                  {...register(name, { required: required })}
                />
                {errors.username && (
                  <p className={styles["form__error"]}>
                    {errors.username?.message}
                  </p>
                )}
              </>
            );
          })}

          <label className={styles["form__label"]}>Username</label>
          <input
            className={styles["form__input"]}
            placeholder="Tomas"
            type="text"
            {...register("username", { required: true })}
          />
          {errors.username && (
            <p className={styles["form__error"]}>{errors.username?.message}</p>
          )}

          <label className={styles["form__label"]}>Password</label>
          <input
            className={styles["form__input"]}
            placeholder="Tomas"
            type="password"
            {...register("password", { required: true })}
          />
          <hr />
          {errors.password && (
            <p className={styles["form__error"]}>{errors.password?.message}</p>
          )}

          <button type="submit" disabled={!isValid}>
            Login
          </button>

          <span>
            Or <a>sign up</a>
          </span>
        </form>
      </div>
    </>
  );
};
