import React, { useEffect, useState } from "react";
import styles from "../login/login.module.scss";
import { Link } from "react-router-dom";
import { useForm } from "react-hook-form";

import { useAuth } from "../../hooks/useAuth";
import SpinnerGif from "../../assets/spinner.gif";

interface User {
  username: string;
  password: string;
  confirmPassword: string;
}

export const Register = () => {
  const {
    register,
    handleSubmit,
    watch,
    setFocus,
    formState: { isValid, errors, isDirty },
  } = useForm<User>({ mode: "onChange" });

  const { signup, isLoading, errorMsg } = useAuth();

  const onSubmit = async (data: User): Promise<void> => {
    signup(data);
  };

  useEffect(() => {
    setFocus("username");
  }, []);

  return (
    <>
      <div className={styles.container}>
        <form className={styles.form} onSubmit={handleSubmit(onSubmit)}>
          <h1 className={styles["form__heading"]}>Register</h1>
          {isLoading && (
            <img className="spinner" src={SpinnerGif} alt="loading" />
          )}
          {errorMsg && <p className={styles["form__error"]}>{errorMsg}</p>}

          <label className={styles["form__label"]}>Username</label>
          <input
            className={styles["form__input"]}
            placeholder="Tomas"
            type="text"
            {...register("username", { required: true })}
          />
          <label className={styles["form__label"]}>Password</label>
          <input
            className={styles["form__input"]}
            placeholder="Tomas"
            type="password"
            {...register("password", {
              required: true,
            })}
          />
          <label className={styles["form__label"]}>Confirm password</label>
          <input
            className={styles["form__input"]}
            placeholder="Tomas"
            type="password"
            {...register("confirmPassword", {
              required: true,
              validate: (val: string) => {
                if (watch("password") !== val) {
                  return "Your passwords do no match";
                }
              },
            })}
          />
          <button
            className={styles["form__button"]}
            type="submit"
            disabled={!isValid}
          >
            Register
          </button>
          <span>
            Or{" "}
            <Link to="/login" className={styles["sign-up-link"]}>
              login
            </Link>
          </span>
          {isLoading && <span>Loading...</span>}
        </form>
      </div>
    </>
  );
};
