import React, { useState } from "react";
import styles from "../login/login.module.scss";
import { useNavigate } from "react-router-dom";
import { useForm } from "react-hook-form";
import { useRegisterMutation } from "../../features/auth/register";
import { setCredentials } from "../../features/auth/authSlice";
import { useDispatch } from "react-redux";

interface User {
  username: string;
  password: string;
  confirmPassword: string;
}

export const Register = () => {
  const {
    register,
    handleSubmit,
    watch,
    formState: { isValid, errors, isDirty },
  } = useForm<User>({ mode: "onChange" });

  const navigate = useNavigate();

  const [registerApi, { isLoading }] = useRegisterMutation();
  const [errMessage, setErrMessage] = useState("");
  const dispatch = useDispatch();

  const onSubmit = async (data: User): Promise<void> => {
    const { username, password } = data;
    console.log(username, password);

    try {
      const userData = await registerApi({ username, password }).unwrap();
      dispatch(setCredentials({ ...userData, username }));
      navigate("/");
    } catch (err: any) {
      console.log("error", err);
      if (err) {
        setErrMessage("No server response");
      } else if (err.response?.status === 400) {
        setErrMessage("Missing username or password");
      } else if (err.response?.status === 403) {
        setErrMessage("Access Denied");
      } else {
        setErrMessage("Login failed");
      }
    }
  };

  return (
    <>
      <div className={styles.container}>
        <form className={styles.form} onSubmit={handleSubmit(onSubmit)}>
          <h1 className={styles["form__heading"]}>Register</h1>
          {errors.confirmPassword && (
            <p className={styles["form__error"]}>
              {errors.confirmPassword?.message}
            </p>
          )}
          {errors.password && (
            <p className={styles["form__error"]}>{errors.password?.message}</p>
          )}
          <label className={styles["form__label"]}>Username</label>
          <input
            className={styles["form__input"]}
            placeholder="Tomas"
            type="text"
            {...register("username", { required: true })}
          />
          <label className={styles["form__label"]}>Password</label>
          <input
            className={styles["form__input"]}
            placeholder="Tomas"
            type="password"
            {...register("password", { required: true })}
          />
          <label className={styles["form__label"]}>Confirm password</label>
          <input
            className={styles["form__input"]}
            placeholder="Tomas"
            type="password"
            {...register("confirmPassword", {
              required: true,
              validate: (val: string) => {
                if (watch("password") != val) {
                  return "Your passwords do no match";
                }
              },
            })}
          />
          <button
            className={styles["form__button"]}
            type="submit"
            disabled={!isValid}
          >
            Register
          </button>
          <span>
            Or{" "}
            <a href="/login" className={styles["sign-up-link"]}>
              login
            </a>
          </span>
        </form>
      </div>
    </>
  );
};
