  createUser,
  useGetPokemonByNameQuery,
  useLoginMutation,
} from "../../features/auth/authApiSlice";
import { useDispatch } from "react-redux";
import { setCredentials } from "../../features/auth/authSlice";

interface User {
  username: string;
  password: string;
}

