import React, { useState } from "react";
import styles from "./movie-card.module.scss";

type Props = {
  movie: {
    title: string;
    description: string;
    releaseYear: number;
    runtime: number;
    genre: string;
    trailer: string;
    image: string;
  };
  setIsModalActive: React.Dispatch<React.SetStateAction<boolean>>;
};

export const MovieCard = ({
  movie: { title, releaseYear, runtime, genre, description, trailer, image },
  setIsModalActive,
}: Props) => {
  return (
    <div className={styles["movie-card"]}>
      <div className={styles["movie-card_left"]}>
        <img src={image} />
      </div>
      <div className={styles["movie-card_right"]}>
        <h1>{title}</h1>
        <div className={styles["movie-card_right__details"]}>
          <ul>
            <li>{releaseYear}</li>
            <li>{runtime} min</li>
            <li>{genre}</li>
          </ul>
        </div>
        <div className={styles["movie-card_right__description"]}>
          <p>{description}</p>
          <button>Read more</button>
          <div className={styles["movie-card_right__link"]}>
            <a href={trailer} className={styles["movie-card_right__button"]}>
              Watch trailer
            </a>
          </div>
        </div>
      </div>
    </div>
  );
};
