import React, { useState } from "react";
import styles from "./movie-card.module.scss";

type Props = {
  movie: {
    title: string;
    description: string;
    releaseYear: number;
    runtime: number;
    genre: string;
    trailer: string;
    image: string;
    moreInformation: string;
  };
};

export const MovieCard = ({
  movie: {
    title,
    releaseYear,
    runtime,
    genre,
    description,
    trailer,
    image,
    moreInformation,
  },
}: Props) => {
  return (
    <div className={styles["movie-card"]}>
      <div className={styles["movie-card_left"]}>
        <img src={image} />
      </div>
      <div className={styles["movie-card_right"]}>
        <h1>{title}</h1>
        <div className={styles["movie-card_right__details"]}>
          <ul>
            <li>{releaseYear}</li>
            <li>{runtime} min</li>
            <li>{genre}</li>
          </ul>
        </div>
        <div className={styles["movie-card_right__description"]}>
          <p>{description}</p>
          <a href={moreInformation}>Read more</a>
          <div className={styles["movie-card_right__link"]}>
            <a
              target="_blank"
              href={trailer}
              className={styles["movie-card_right__button"]}
            >
              Watch trailer
            </a>
          </div>
        </div>
      </div>
    </div>
  );
};
